mapper.insert:
INSERT INTO Board (
	title, videoId, videoDetail, date
) VALUES (
	#{title}, #{videoId}, #{videoDetail}, #{date}
)

mapper.select:
SELECT boardId
FROM Board
WHERE title = #{title} AND videoId = #{videoId} AND love = #{love} AND dislike = #{dislike} AND date = #{date}

mapper.update:
UPDATE Board
SET love = love + 1
WHERE boardId = #{boardId}

UPDATE Board
SET dislike = dislike + 1
WHERE boardId = #{boardId}

UPDATE Board
SET viewed = viewed + 1
WHERE boardId = #{boardId}
