mapper.insert:
INSERT INTO Upcoming (
	memberName, channelId, videoId, scheduledStartTime, thumbnailPath, apiKey
) VALUES (
	#{memberName}, #{channelId}, #{videoId}, #{scheduledStartTime}, #{thumbnailPath}, #{apiKey}
)

mapper.update:
UPDATE Upcoming
SET scheduledStartTime = #{scheduledStartTime}
WHERE videoId = #{videoId}

